declare module '*.png' {
  const content: any;
  export default content;
}

declare module '*.scss' {
  const css: any;
  export default css;
}

declare module '*.woff' {
  const font: any;
  export default font;
}

declare module '*.woff2' {
  const font: any;
  export default font;
}

declare module '*.svg' {
  const svg: any;
  export default svg;
}

declare module '*.json' {
  const value: any;
  export default value;
}
